        else if(value == "stops") {
            node.Style.Gradient.Stops = new DiagramObjectCollection<GradientStop>()
            {
                new GradientStop(){ Color= "#dc234e", Offset=0},
                new GradientStop(){Color ="#ebc614", Offset=30},
                new GradientStop(){Color ="#4a9d7e", Offset=70}
